import React, { useEffect, useState } from 'react';
import queryString from 'query-string';
import io from 'socket.io-client';
import { useLocation } from 'react-router-dom';
import Infobar from '../Infobar/Infobar';
import Input from '../Input/Input';
import Messages from '../Messages/Messages';
import TextContainer from '../TextContainer/TextContainer';
import './Chat.css';

let socket;

const Chat = () => {
    const location = useLocation();
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [users, setUsers] = useState([]);
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [scheduleTime, setScheduleTime] = useState('');
    const ENDPOINT = 'http://localhost:5000';

    useEffect(() => {
        const { name, room } = queryString.parse(location.search);
        socket = io(ENDPOINT);
        setName(name);
        setRoom(room);

        socket.emit('join', { name, room }, (error) => {
            if (error) {
                alert(error);
            }
        });

        socket.on('message', (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
        });

        socket.on("roomData", ({ users }) => {
            setUsers(users);
        });

        useEffect(() => {
            socket.on('message', (message) => {
                setMessages((prevMessages) => [...prevMessages, message]);
                // Notify server that this message is seen
                if (message.user !== name) { // Only mark as seen if the message is from another user
                    socket.emit('markAsSeen', message.id); // Assume each message has a unique ID
                }
            });
        
            socket.on('updateMessageSeen', (updatedMessage) => {
                setMessages((prevMessages) => 
                    prevMessages.map((msg) => 
                        msg.id === updatedMessage.id ? { ...msg, seen: true } : msg
                    )
                );
            });
        }, [name, messages]);
        

        return () => {
            socket.disconnect();
            socket.off();
        };
    }, [ENDPOINT, location.search]);

    const sendMessage = (event) => {
        event.preventDefault();
        if (message) {
            const msgId = messages.length; // Generate a message ID based on the current message length
            const msgObj = { id: msgId, user: name, text: message, seen: false }; // Include id and seen status

            socket.emit('sendMessage', msgObj, () => {
                setMessage('');
                setScheduleTime('');
            });
        }
    };

    const handleMessageSeen = (messageIndex) => {
        socket.emit('seenMessage', messageIndex); // Emit seen event
    };

    useEffect(() => {
        if (messages.length > 0) {
            handleMessageSeen(messages.length - 1); // Mark the latest message as seen
        }
    }, [messages]);

    return (
        <div className='outerContainer'>
            <div className='container'>
                <Infobar room={room} />
                <Messages messages={messages} name={name} />
                <Input 
                    message={message} 
                    setMessage={setMessage} 
                    sendMessage={sendMessage} 
                    scheduleTime={scheduleTime} 
                    setScheduleTime={setScheduleTime} 
                />
            </div>
            <TextContainer users={users} />
        </div>
    );
};

export default Chat;
