import React, { useRef } from 'react';
import { FaCalendarAlt } from 'react-icons/fa';
import './Input.css';

const Input = ({ message, setMessage, sendMessage, scheduleTime, setScheduleTime }) => {
    const dateInputRef = useRef(null); // Reference to the hidden date input

    const handleIconClick = () => {
        if (dateInputRef.current) {
            dateInputRef.current.showPicker(); // Opens the date picker on icon click
        }
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        sendMessage(event, scheduleTime); // Send message with the scheduled time
    };

    return (
        <form className='form' onSubmit={handleSubmit}>
            <input
                className='input'
                type='text'
                placeholder="Type a message..."
                value={message}
                onChange={(event) => setMessage(event.target.value)}
            />
            <button className='sendButton' type='submit'>Send</button>

            {/* Hidden Date Input */}
            <input
                type='datetime-local'
                className='dateInput'
                ref={dateInputRef}
                value={scheduleTime}
                onChange={(event) => setScheduleTime(event.target.value)}
                style={{ display: 'none' }} // Hide the input field
            />

            {/* Calendar Icon */}
            <div className='datePickerContainer' onClick={handleIconClick}>
                <FaCalendarAlt className='dateIcon' />
            </div>
        </form>
    );
};

export default Input;
