import React, { useState, useRef, useEffect } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import { FaCalendarAlt, FaPaperPlane, FaSmile } from 'react-icons/fa';
import EmojiPicker from 'emoji-picker-react';
import './Input.css';

const Input = ({ message, setMessage, sendMessage, scheduleTime, setScheduleTime }) => {
    const [showEmojiPicker, setShowEmojiPicker] = useState(false);
    const emojiPickerRef = useRef(null); 

    const handleIconClick = () => {
        setShowEmojiPicker(false);
    };

    const handleDateChange = (date) => {
        setScheduleTime(date); // Update schedule time when date changes
    };

    const onEmojiClick = (emojiObject) => {
        setMessage((prevMessage) => prevMessage + emojiObject.emoji);
        setShowEmojiPicker(false); // Close the emoji picker after selecting
    };

    // Effect to handle clicks outside the emoji picker
    useEffect(() => {
        const handleClickOutside = (event) => {
            if (emojiPickerRef.current && !emojiPickerRef.current.contains(event.target)) {
                setShowEmojiPicker(false); // Close the emoji picker
            }
        };

        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, [emojiPickerRef]);

    return (
        <form className='form' onSubmit={(e) => { e.preventDefault(); sendMessage(e); }}>
            <input
                className='input'
                type='text'
                placeholder="Type a message..."
                value={message}
                onChange={(event) => setMessage(event.target.value)}
            />
            <button className='sendButton' type='submit'><FaPaperPlane /></button>

            <div className='datePickerContainer'>
                <FaCalendarAlt className='dateIcon' onClick={() => setShowEmojiPicker(false)} />
                <DatePicker
                    selected={scheduleTime}
                    onChange={handleDateChange}
                    showTimeSelect
                    dateFormat="Pp"
                />
            </div>

            <FaSmile className="emojiIcon" onClick={() => setShowEmojiPicker(!showEmojiPicker)} />
            {showEmojiPicker && (
                <div className="emojiPickerContainer" ref={emojiPickerRef}>
                    <EmojiPicker onEmojiClick={onEmojiClick} />
                </div>
            )}
        </form>
    );
};

export default Input;
