import React, { useEffect, useState } from 'react';
import queryString from 'query-string';
import io from 'socket.io-client';
import { useLocation } from 'react-router-dom';
import Infobar from '../Infobar/Infobar';
import Input from '../Input/Input';
import Messages from '../Messages/Messages';
import TextContainer from '../TextContainer/TextContainer';
import './Chat.css';

let socket;

const Chat = () => {
    const location = useLocation();
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [users, setUsers] = useState([]);
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [scheduleTime, setScheduleTime] = useState('');
    const [scheduledMessages, setScheduledMessages] = useState([]);
    const ENDPOINT = 'http://localhost:5000';

    useEffect(() => {
        const { name, room } = queryString.parse(location.search);
        socket = io(ENDPOINT);
        setName(name);
        setRoom(room);

        socket.emit('join', { name, room }, (error) => {
            if (error) {
                alert(error);
            }
        });

        socket.on('message', (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
        });

        socket.on("roomData", ({ users }) => {
            setUsers(users);
        });

        return () => {
            socket.disconnect();
            socket.off();
        };
    }, [ENDPOINT, location.search]);

    const sendMessage = (event) => {
        event.preventDefault();
        if (message) {
            const currentTime = new Date();
            const scheduledTime = new Date(scheduleTime);

            if (scheduledTime > currentTime) {
                const timeDiff = scheduledTime - currentTime;
                
                // Add message to scheduled messages list
                setScheduledMessages((prevScheduled) => [
                    ...prevScheduled,
                    { message, time: scheduledTime.toLocaleString() }
                ]);

                setTimeout(() => {
                    socket.emit('sendMessage', message, () => {
                        setMessage('');
                        setScheduleTime('');
                    });
                }, timeDiff);
            } else {
                socket.emit('sendMessage', message, () => {
                    setMessage('');
                    setScheduleTime('');
                });
            }
        }
    };

    return (
        <div className='outerContainer'>
            <div className='container'>
                <Infobar room={room} />

                {/* Scheduled Messages Dropdown */}
                <div className="scheduledMessagesDropdown">
                    <h3>Scheduled Messages</h3>
                    {scheduledMessages.length > 0 ? (
                        scheduledMessages.map((msg, index) => (
                            <div key={index} className="scheduledMessage">
                                <p>{msg.time}</p>
                                <p>{msg.message}</p>
                            </div>
                        ))
                    ) : (
                        <p>No scheduled messages</p>
                    )}
                </div>

                <Messages messages={messages} name={name} />
                <Input 
                    message={message} 
                    setMessage={setMessage} 
                    sendMessage={sendMessage} 
                    scheduleTime={scheduleTime} 
                    setScheduleTime={setScheduleTime} 
                />
            </div>
            <TextContainer users={users} />
        </div>
    );
};

export default Chat;
