import React, { useEffect, useState } from 'react';
import queryString from 'query-string';
import io from 'socket.io-client';
import { useLocation } from 'react-router-dom';
import Infobar from '../Infobar/Infobar';
import Input from '../Input/Input';
import Messages from '../Messages/Messages';
import TextContainer from '../TextContainer/TextContainer';
import './Chat.css';

let socket;

const Chat = () => {
    const location = useLocation();
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [users, setUsers] = useState([]);
    const [message, setMessage] = useState('');
    const [messages, setMessages] = useState([]);
    const [scheduleTime, setScheduleTime] = useState('');
    const ENDPOINT = 'http://localhost:5000';

    useEffect(() => {
        const { name, room } = queryString.parse(location.search);

        // Avoid reinitializing the socket if already connected
        if (!socket) {
            socket = io(ENDPOINT);
        }

        setName(name);
        setRoom(room);

        // Join the room
        socket.emit('join', { name, room }, (error) => {
            if (error) {
                alert(error);
            }
        });

        // Listen for messages and room data, but only add listeners once
        socket.on('message', (message) => {
            setMessages((prevMessages) => [...prevMessages, message]);
            // Notify server that this message is seen if it's from another user
            if (message.user !== name) {
                socket.emit('markAsSeen', message.id); // Assuming messages have unique IDs
            }
        });

        socket.on("roomData", ({ users }) => {
            setUsers(users);
        });

        socket.on('updateMessageSeen', (updatedMessage) => {
            setMessages((prevMessages) => 
                prevMessages.map((msg) => 
                    msg.id === updatedMessage.id ? { ...msg, seen: true } : msg
                )
            );
        });

        // Clean up listeners on component unmount
        return () => {
            socket.off('message');
            socket.off('roomData');
            socket.off('updateMessageSeen');
            socket.disconnect();
            socket = null; // Clear the socket variable
        };
    }, [ENDPOINT, location.search]);

    const sendMessage = (event) => {
        event.preventDefault();
        if (message) {
            const currentTime = new Date();
            const scheduledTime = new Date(scheduleTime);

            if (scheduledTime > currentTime) {
                const timeDiff = scheduledTime - currentTime;
                setTimeout(() => {
                    socket.emit('sendMessage', message, () => {
                        setMessage('');
                        setScheduleTime('');
                    });
                }, timeDiff);
            } else {
                socket.emit('sendMessage', message, () => {
                    setMessage('');
                    setScheduleTime('');
                });
            }
        }
    };

    return (
        <div className='outerContainer'>
            <div className='container'>
                <Infobar room={room} />
                <Messages messages={messages} name={name} />
                <Input 
                    message={message} 
                    setMessage={setMessage} 
                    sendMessage={sendMessage} 
                    scheduleTime={scheduleTime} 
                    setScheduleTime={setScheduleTime} 
                />
            </div>
            <TextContainer users={users} />
        </div>
    );
};

export default Chat;
