import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css'; // Import styles for the date picker
import './Input.css';

// You can import an icon from a library, e.g., Font Awesome
import { FaCalendarAlt } from 'react-icons/fa'; // Example icon

const Input = ({ message, setMessage, sendMessage }) => {
    const [scheduleDate, setScheduleDate] = useState(null);

    const handleSubmit = (event) => {
        event.preventDefault();
        if (scheduleDate) {
            const scheduledTime = scheduleDate.getTime();
            sendMessage(event, scheduledTime); // Pass scheduled time to sendMessage
        } else {
            sendMessage(event); // Send message immediately if no date is set
        }
    };

    return (
        <form className='form' onSubmit={handleSubmit}>
            <input
                className='input'
                type='text'
                placeholder="Type a message..."
                value={message}
                onChange={(event) => setMessage(event.target.value)}
                onKeyDown={event => event.key === 'Enter' ? handleSubmit(event) : null}
            />
            <button className='sendButton' type='submit'>Send</button>
            
            {/* Date Picker */}
            <div className='datePickerContainer'>
                <FaCalendarAlt className='dateIcon' /> {/* Calendar icon */}
                <DatePicker
                    selected={scheduleDate}
                    onChange={date => setScheduleDate(date)}
                    showTimeSelect
                    dateFormat="Pp"
                    placeholderText="Schedule Time"
                    className='datePicker'
                />
            </div>
        </form>
    );
};

export default Input;
