    return (
        <div>
            {messages.map((message, index) => (
                <div key={index} className={message.user === name ? 'messageContainer justifyEnd' : 'messageContainer justifyStart'}>
                    <p className={message.user === name ? 'sentText' : 'receivedText'}>
                        {message.user}: {message.text}
                        {message.seen && <span className="seenStatus">âœ“</span>}
                    </p>
                </div>
            ))}
        </div>
    );
};

export default Messages;
