import React, { useState } from 'react';
import DatePicker from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './Input.css';
import { FaCalendarAlt } from 'react-icons/fa';

const Input = ({ message, setMessage, sendMessage }) => {
    const [scheduleDate, setScheduleDate] = useState(null);
    const [isDatePickerOpen, setDatePickerOpen] = useState(false);

    const handleIconClick = () => {
        setDatePickerOpen(true); // Opens the calendar
    };

    const handleDateChange = (date) => {
        setScheduleDate(date);
        setDatePickerOpen(false); // Close calendar after selecting a date
    };

    return (
        <form className='form' onSubmit={(e) => { e.preventDefault(); sendMessage(e, scheduleDate); }}>
            <input
                className='input'
                type='text'
                placeholder="Type a message..."
                value={message}
                onChange={(event) => setMessage(event.target.value)}
            />
            <button className='sendButton' type='submit'>Send</button>

            <div className='datePickerContainer'>
                <FaCalendarAlt className='dateIcon' onClick={handleIconClick} /> {/* Clickable icon */}
                {isDatePickerOpen && (
                    <DatePicker
                        selected={scheduleDate}
                        onChange={handleDateChange}
                        onClickOutside={() => setDatePickerOpen(false)} // Close on outside click
                        showTimeSelect
                        dateFormat="Pp"
                        // inline // Displays the calendar inline directly below the icon
                    />
                )}
            </div>
        </form>
    );
};

export default Input;
